#!/bin/ruby
# Find substring of length k with the most vowels

# It timed out. 8/13 tests passed. :(
def findSubstring(s, k)
    vowels = "aeiou"
    v = Array.new(s.size, 0)
    maxcount = 0
    maxstring = ''
    s.size.times do |i|
        v[i] = 1 if vowels.include?(s[i])
    end

    (0..s.size - k).each do |i|
        next if v[i] == 0
        count = 0
        substr = s[i..i + k - 1]
        (i..i + k - 1).each do |j|
            count += 1 if v[j] == 1
        end

        if count > maxcount
            maxcount = count
            maxstring = substr
        end
    end
    return 'Not found!' if maxstring == ''
    maxstring
end

k = 621
s = ''

puts findSubstring(s, k)
